const sampleSQL = `-- Churn Prediction Data Pipeline
-- Auto-generated by VDS Co-Pilot

-- Step 1: Data Cleanup
WITH cleaned_customers AS (
  SELECT 
    customer_id,
    email,
    COALESCE(full_name, 'Unknown') AS full_name,
    created_at,
    CASE 
      WHEN segment IS NULL THEN 'Standard'
      ELSE segment 
    END AS segment
  FROM raw.customers
  WHERE created_at >= DATEADD(month, -12, CURRENT_DATE)
    AND email IS NOT NULL
),

-- Step 2: Transaction Features
transaction_features AS (
  SELECT 
    c.customer_id,
    COUNT(t.id) AS total_orders,
    SUM(t.amount) AS total_spend,
    AVG(t.amount) AS avg_order_value,
    MAX(t.created_at) AS last_order_date,
    DATEDIFF(day, MAX(t.created_at), CURRENT_DATE) AS days_since_last_order
  FROM cleaned_customers c
  LEFT JOIN raw.transactions t ON c.customer_id = t.customer_id
  GROUP BY c.customer_id
),

-- Step 3: Session Features  
session_features AS (
  SELECT
    customer_id,
    COUNT(*) AS total_sessions,
    AVG(duration_seconds) / 60.0 AS avg_session_duration_mins,
    SUM(page_views) AS total_page_views
  FROM raw.sessions
  WHERE created_at >= DATEADD(day, -90, CURRENT_DATE)
  GROUP BY customer_id
),

-- Step 4: Label Definition
labeled_customers AS (
  SELECT
    c.*,
    tf.*,
    sf.*,
    CASE 
      WHEN tf.days_since_last_order >= 30 THEN 1 
      ELSE 0 
    END AS is_churned
  FROM cleaned_customers c
  LEFT JOIN transaction_features tf USING (customer_id)
  LEFT JOIN session_features sf USING (customer_id)
)

-- Final Output
SELECT * FROM labeled_customers
ORDER BY customer_id;`;

